mapper.insert:
	insert into tb_company_role(
		role_desc,
		role_name
	)
	values(
		#{roleName},
		#{roleName}
	)

	select LAST_INSERT_ID() as id

mapper.update:
update tb_company_role
set 
	role_name=#{roleName},
	role_desc=#{roleName}

where id=#{id}

mapper.insert:
insert into tb_user_role(
	id,
	title
)
values(
	(select max(id)+1 from tb_user_role),
	#{text}
)

mapper.update:
update tb_user_role
set 
	title=#{text}

where id=#{id}
